import React from "react";
import DrawerRouterContainer from "./layout/DrawerRouterContainer";
import axios from 'axios';
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "./styles/_home.scss";
import { Button } from 'react-bootstrap';
import {withRouter} from "react-router-dom";
import Qs from 'qs';
import { element } from "prop-types";


const Dashboard = (props)=> {

    const location = useLocation();
    let username;
    let obj;
    let elements=[];
    let avg_speed;
    const [activities, setActivities] = useState([] as any)
    if (location.state){
        console.log(props)
        username = location.state.username;
        console.log('user is', username);
    }

    const getActivity = () => {
        let bodyFormData = new FormData();
        bodyFormData.set('username', username);
        // axios.get("https://coachingmate-backend2020.herokuapp.com/auth/getAllByUsername?username=${username}", {
        //     headers: {
        //                 "Access-Control-Allow-Origin": "*",
        //                 "Accept": 'application/json',
        //                 "Content-Type": "application/json",
        //                 "Access-Control-Allow-Methods": "POST, GET, OPTIONS, DELETE, PUT",
        //             },
        //     params: {
        //         username: Qs.stringify(username),
        //     }
        //   })
        axios({
            method: "POST",
            url: "https://coachingmate-backend2020.herokuapp.com/getAllByUsername",

            headers: {
                "Access-Control-Allow-Origin": "*",
                "Accept": 'application/json',
                "Content-Type": "application/json",
                "Access-Control-Allow-Methods": "POST, GET, OPTIONS, DELETE, PUT",
            },
            data: bodyFormData,
            // data:{
            //     username: Qs.stringify(username),
            // },
        })
        .then(function(res){
            // console.log(res.headers)
            console.log('This is ', username)
            console.log("Activities", res)
            obj = res.data;
            Object.keys(obj).map(key => {
                // console.log(key,obj[key]);
                elements = obj[key];
                avg_speed = elements['avg_speed'];
                console.log('hello',elements)
                // Object.keys(i).map(item =>{
                //     elements= elements.concat(i,i[item]);

                //     console.log("iiii",elements) 
                //     // console.log("iiii",i[item]) 
                // })
            });

        //     obj.map(key =>{
        //         elements=elements.concat(obj[key]);
        //         console.log('eeeee',elements)})
        //     console.log("SetActivities", activities);
        })
           .catch(error=>
            console.log("error", error))

    }


    return (
        (username==null) ? (
            <a href="/">
                <h2>Please log in first! :)</h2>
            </a>) : (
        <DrawerRouterContainer>
            <div style={{ backgroundColor: "#C7EDCC" }}>
                <h2>
                    <b>Hello {username}, Welcome to Garmin Dashboard</b>
                </h2>

                <Button 
                    variant="info"
                    className="button-home"
                    onClick={getActivity}
                    >
                        View all activity data
                </Button>

                <h4>{avg_speed}</h4>

            </div>
        </DrawerRouterContainer>)
    );
}

export default withRouter(Dashboard)
