import React from "react";
import DrawerRouterContainer from "./layout/DrawerRouterContainer";
import axios from 'axios';
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "./styles/_home.scss";
import { Button } from 'react-bootstrap';
import {withRouter} from "react-router-dom";


const Dashboard = (props)=> {

    const location = useLocation();
    let username;
    const [activities, setActivities] = useState([])
    if (location.state){
        console.log(props)
        username = location.state.username;
        console.log('user is', username);
    }

    const getActivity = () => {
        if(username){
        axios({
            method: "get",
            url: "https://coachingmate-backend2020.herokuapp.com/activity/getAllByUsername",
            // url:"http://localhost:8080/activity/getAllByUsername",
            // url:"https://coachingmate-backend2020.herokuapp.com/requestToken",

            headers: {
                "Access-Control-Allow-Origin": "*",
                "Accept": 'application/json',
                "Content-Type": "application/json",
                "Access-Control-Allow-Methods": "POST, GET, OPTIONS, DELETE, PUT",
            },
            data:{
                // username: username,
                username:'joey',
            },
        }).then(function(res){
            // console.log(res.headers)
            console.log('This is ', username)
            console.log("Activities", res)
            setActivities(res.data)
            console.log("SetActivities", activities)

        })
           .catch(error=>
            console.log("error", error))
        }
    }

    return (
        (username==null) ? (
            <a href="/">
                <h2>Please log in first! :)</h2>
            </a>) : (
        <DrawerRouterContainer>
            <div style={{ backgroundColor: "#C7EDCC" }}>
                <h2>
                    <b>Hello {username}, Welcome to Garmin Dashboard</b>
                </h2>

                <Button 
                    variant="info"
                    className="button-home"
                    onClick={getActivity}
                    >
                        View all activity data
                </Button>

               <p>{activities}</p>



            </div>
        </DrawerRouterContainer>)
    );
}

export default withRouter(Dashboard)
