import React from "react";
//import { useHistory } from "react-router";
//import PropTypes from 'prop-types';
import { Form, Field, FormElement } from "@progress/kendo-react-form";
import { Error } from "@progress/kendo-react-labels";
//import { user } from "./layout/DrawerRouterContainer";
import { Input } from "@progress/kendo-react-inputs";
import {connectBackends}  from "./services/dataService";
//import {browserHistory} from "./index"


const newInput = (fieldRenderProps) => {
    const { validationMessage, visited, ...others } = fieldRenderProps;
    return (
        <div>
        <Input {...others} />
        {fieldRenderProps.visited && fieldRenderProps.validationMessage && (
            <Error>{validationMessage}</Error>
            )}
        </div>
        );
};


export default class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username:''
        };
    }
    clickEvent = (e) => {
        connectBackends('http://localhost:8080/login?username='+e.username+'&password='+e.password).then((data)=>{
            this.setState({username:data})
        })
        window.location.href="/Home"
    }

    render(){
        return (
            <Form
            onSubmit={this.clickEvent}
            render={(formRenderProps) => (
                <FormElement
                style={{
                    backgroundColor: "#C7EDCC",
                    textAlign: "center",
                    maxWidth: 3000,
                }}
                >
                <fieldset className={"k-form-fieldset"}>
                <legend>
                <i>Please fill in the fields:</i>
                </legend>
                <div className="mb-3">
                <Field
                name={"username"}
                component={newInput}
                label={"username"}
                validator={(value) =>
                    new RegExp(/abc/).test(value)
                    ? ""
                    : "Please enter a valid Username."
                }
                />
                </div>
                <div className="mb-3">
                <Field
                name={"password"}
                component={newInput}
                type={"password"}
                label={"password"}
                validator={(value) =>
                    new RegExp(/123/).test(value)
                    ? ""
                    : "Please enter a valid Password."
                }
                />
                </div>
                </fieldset>
                <div className="k-form-buttons">
                <button
                type={"submit"}
                className="k-button"
                disabled={!formRenderProps.allowSubmit}
                >
                Submit
                </button>
                </div>
                </FormElement>
                )}
            />
            );
    }
}
