import React, {useState, Component} from "react";
import axios from "axios";


class Login extends Component {
    constructor(props){
        super(props);

        this.state={
            username: "",
            password: ""
        }
    }

    handleChange (e) {
        this.setState({[e.target.name]: e.target.value})
    }

    handleSubmit(e){
        const {username, password} = this.state;

        axios
        .post(
            "https://coachingmate-backend2020.herokuapp.com/login",
            {
                user:{
                    username: username,
                    password: password,
                }
            }
        )
        .then(res=>{
            console.log("response",res)
        })
        .catch(error=>
            console.log("error", error))
    }

    render(){
        return(
            <div>
                <h2>Welcome to CM dasboard</h2>
            </div>
        )
    }

}

export default Login;
