import React, {useState, Component} from "react";


class Login extends Component {
    constructor(props){
        super(props);

        this.state={
            username: "",
            password: ""
        }
    }

    handleChange (e) {
        this.setState({[e.target.name]: e.target.value})
    }

}

export default Login;
